class VectorStoreClient:
    def __init__(self, base_url="https://api.openai.com", api_key=None):
        self.base_url = base_url.rstrip('/')
        self.api_key = api_key
        self.session = requests.Session()
        self.session.headers.update({
            'Authorization': f'Bearer {api_key}',
            'Content-Type': 'application/json',
            'OpenAI-Beta': 'assistants=v2'
        })

    def _validate_vector_store_id(self, vs_id):
        """Validate vector store ID format"""
        import re
        pattern = r'^vs_[a-zA-Z0-9]{24,}$'
        if not re.match(pattern, vs_id):
            raise ValueError(f"Invalid vector store ID format: {vs_id}")
        return vs_id

    def _construct_url(self, endpoint, vs_id=None):
        """Construct and validate API URLs"""
        if vs_id:
            vs_id = self._validate_vector_store_id(vs_id)
            return f"{self.base_url}/v1/vector_stores/{vs_id}/{endpoint}"
        return f"{self.base_url}/v1/vector_stores"

