from dataclasses import dataclass, field
from datetime import datetime
from enum import Enum, auto
from typing import List, Set, Optional


class BadgeType(Enum):
    LLM = auto()
    VLM = auto()
    RAG = auto()
    AGENT = auto()
    ACL25_MAIN = auto()


@dataclass
class Paper:
    title: str
    url: str
    date: datetime
    badges: Set[BadgeType]

    def get_badge_colors(self) -> dict:
        """Return the color code for each badge type"""
        return {
            BadgeType.LLM: "589cf4",
            BadgeType.VLM: "c7688b",
            BadgeType.RAG: "87b800",
            BadgeType.AGENT: "87b800",
            BadgeType.ACL25_MAIN: "f1b800"
        }

    def format_badges(self) -> str:
        """Format badges for display"""
        badge_colors = self.get_badge_colors()
        badges_html = []

        for badge in self.badges:
            badge_name = badge.name
            if badge == BadgeType.ACL25_MAIN:
                badge_name = "ACL'25_Main"
            color = badge_colors[badge]
            badges_html.append(f"![{badge_name}](https://img.shields.io/badge/{badge_name}-{color})")

        return " ".join(badges_html)

    def format_entry(self) -> str:
        """Format the paper entry for display"""
        date_str = self.date.strftime("%Y/%m")
        badges = self.format_badges()
        return f"- [{date_str}] **[{self.title}]({self.url})** {badges}"


@dataclass
class PoisonBackdoorCollection:
    papers: List[Paper] = field(default_factory=list)

    def add_paper(self, title: str, url: str, date_str: str, badges: List[str]):
        """Add a paper to the collection"""
        # Parse date
        date = datetime.strptime(date_str, "%Y/%m")

        # Parse badges
        badge_set = set()
        for badge in badges:
            if badge.upper() == "LLM":
                badge_set.add(BadgeType.LLM)
            elif badge.upper() == "VLM":
                badge_set.add(BadgeType.VLM)
            elif badge.upper() == "RAG":
                badge_set.add(BadgeType.RAG)
            elif badge.upper() == "AGENT":
                badge_set.add(BadgeType.AGENT)
            elif badge.upper() == "ACL'25_MAIN":
                badge_set.add(BadgeType.ACL25_MAIN)

        # Create and add paper
        paper = Paper(title=title, url=url, date=date, badges=badge_set)
        self.papers.append(paper)

    def get_sorted_papers(self) -> List[Paper]:
        """Get papers sorted by date (newest first)"""
        return sorted(self.papers, key=lambda p: p.date, reverse=True)

    def display_collection(self) -> str:
